INSERT INTO jokes VALUES (287, 'Why are teachers happy at Halloween parties? Because there''s lots of school spirit!');
INSERT INTO jokes VALUES (288, 'What happened to the girl who wore a mouse costume to her Halloween party? The cat ate her.');
