INSERT INTO jokes VALUES (1691, 'Did you hear about the tree''s birthday? It was a sappy one!');
INSERT INTO jokes VALUES (1692, 'Why won''t anyone eat the dogs birthday cake? Because he always slobbers out the candles!');
INSERT INTO jokes VALUES (1693, '"I''m giving a ''surprised'' birthday party for you." "A ''surprised''. birthday party? What''s that?" "That''s where I invite a bunch of your friends, and if any of them come, I''ll be surprised!"');
INSERT INTO jokes VALUES (1694, 'Did you hear about the dancer''s birthday? It was a tappy one!');
INSERT INTO jokes VALUES (1695, 'How can you tell if an elephant''s been to your birthday party? Look for his footprints in the ice cream.');
