INSERT INTO jokes VALUES (1701, 'Man l: "I got my wife a VCP for her birthday" Man 2: "Don''t you mean a VCR?" Man 1: "No, a VCP . . . Very Cheap Present!"');
INSERT INTO jokes VALUES (1702, 'What does a cat like to eat on his birthday? Mice cream and cake!');
