INSERT INTO jokes VALUES (1704, '"Were any famous men born on your birthday?" "No, only little babies."');
INSERT INTO jokes VALUES (1705, 'For his birthday the monster asked for a heavy sweater. So they gave him a sumo wrestler!');
INSERT INTO jokes VALUES (1706, 'Why did the boy feel warm on his birthday? Because people kept toasting him!');
INSERT INTO jokes VALUES (1707, 'Why was the birthday cake as hard as a rock? Because it was marble cake!');
INSERT INTO jokes VALUES (1708, 'Cat: "What did you get him for his birthday?" Dog: "Pant . . . pant!" Cat: "Great . . . he needs a pair of pants!"');
INSERT INTO jokes VALUES (1709, 'What do you always get on your birthday? Another year older!birt');
