INSERT INTO jokes VALUES (2340, 'Cross-Eyed Monster: When I grow up I want to be a bus driver. Witch: Well, I won''t stand in your way.');
INSERT INTO jokes VALUES (2341, 'Q: What is a bus ?A: A bus is a vehicle that runs twice as fast when you are after it as when you are in it.');
INSERT INTO jokes VALUES (2342, 'Why didn''t anyone take the school bus to school?I wouldn''t fit through the door.');
INSERT INTO jokes VALUES (2343, 'How can you help a starving cannibal? Give him a helping hand.');
INSERT INTO jokes VALUES (2344, 'What happened when the cannibals ate a comedian? They had a feast of fun.');
INSERT INTO jokes VALUES (2345, 'What happens if you upset a cannibal? You get into hot water.');
INSERT INTO jokes VALUES (2346, 'What did the cannibal say when he came home and found his wife chopping up a python and a pygmy? Oh no, not snake and pygmy pie again!');
INSERT INTO jokes VALUES (2347, 'What did the cannibal say when he was full? I couldn''t eat another mortal.');
INSERT INTO jokes VALUES (2348, 'Why was the cannibal fined by the judge? He was caught poaching.');
INSERT INTO jokes VALUES (2349, 'What happened when the cannibal ate the speaking clock? It repeated on him.');
