INSERT INTO jokes VALUES (4166, 'What do you call a black Eskimo dog ?A dusky husky !');
INSERT INTO jokes VALUES (4167, 'What do you call a sheepdog''s tail that can tell tall stories ?A shaggy dogs tale !');
INSERT INTO jokes VALUES (4168, 'Why did the dog have a gleam in his eye?Someone bumped his elbow while he was brushing his teeth.');
INSERT INTO jokes VALUES (4169, 'Why do dogs run in circles ?Because its hard to run in squares !');
INSERT INTO jokes VALUES (4170, 'How do you find your dog if he''s lost in the woods ?Put your ear up to a tree and listen for the bark !');
INSERT INTO jokes VALUES (4171, 'How can you tell a dog from a tomato?The tomato is red.');
INSERT INTO jokes VALUES (4172, 'How can you tell a dog from an elephant?The elephant remembers.');
INSERT INTO jokes VALUES (4173, 'How did the dog feel when he lost his flashlight?Delighted.');
INSERT INTO jokes VALUES (4174, 'How did the dog get into the locked cemetery at night?He used a skeleton key.');
INSERT INTO jokes VALUES (4175, 'How did the dog make anti-freeze?He stole her blanket.');
