INSERT INTO jokes VALUES (4178, 'How do you keep a dog from barking in your front yard?Put him in your back yard.');
INSERT INTO jokes VALUES (4179, 'What did the dog do when the panhandler put the bite on him?Bit him, naturally.');
INSERT INTO jokes VALUES (4180, 'What did the dog do with the history professor?They got together and talked over old times.');
