INSERT INTO jokes VALUES (9561, 'Nate: "Hey, what''s the weather like out there?" Kate: "I don''t know. I''ll tell you when it clears."');
INSERT INTO jokes VALUES (9562, 'First cave man to 2nd cave man: "I don''t care what you say. We never had such unusual weather before they started using bows and arrows."');
